let poll = setTimeout(function(){ autoRefresh(); }, 1000);

function submitform(){
  document.getElementById('qrCodeClaimed').click();
}

function autoRefresh(){
  const status = document.getElementById('status');
  if (status?.textContent === 'REQUESTED') {
    document.getElementById('status-description').style.display="block";
  }
  if (status?.textContent === 'IN_PROGRESS') {
    document.getElementById('status').textContent = 'IN_PROGRESS';
  }
  clearTimeout(poll);
  poll = setTimeout(function(){ submitform(); autoRefresh(); }, 4000);
}

