  if (status?.textContent === 'IN_PROGRESS') {
    document.getElementById('status').textContent = 'IN_PROGRESS';
  }
  clearTimeout(poll);
  poll = setTimeout(function(){ submitform(); autoRefresh(); }, 4000);
}

