settings.css:
.page__content {
    margin: 0px !important;
}
.link-text {
    background-color: transparent !important;
    border: none;
}
.link-text:hover {
    background-color: transparent !important;
    text-decoration: underline;
}
.left-text-block {
    text-align:left !important
}
button.tile-button--side-icon div {
    display: inherit !important;
}
.text-left-to-icon {
    margin-left: 0.5em;
    vertical-align: super;
}
.field-message {
    font-size: 13px;
    min-width: auto;
}
.picon-error-triangle:before {
    content: "\E914";
    font-family: iconfont!important;
    speak: none;
    font-style: normal;
    font-weight: 400;
    font-variant: normal;
    text-transform: none;
    line-height: 1;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    margin-right: 5px;
}

/* only for default */
body, .page{
  background-color: #ededed !important;
}
.card__content {
    padding: 50px 20px !important;
    position: relative;
    background-color: #767676 !important;
}

.branding-template-primary-text,
.branding-template-heading,
.text-block--overflow-wrap {
    color: #ffffff !important;
    text-align: left !important;
    margin-bottom: 15px !important;
}
.text-input, .dropdown__input, .dropdown__list, .dropdown {
    color: #ffffff !important;
    border-color: #4f4f4f !important;
    background-color: #646464 !important;
}
.dropdown__option{
    color: #ffffff !important;
    border-color: #4f4f4f !important;
}
.dropdown__option:hover {
    color: #fff !important;
    background: #a8a5a5 !important;
}

.branding-template-heading .text-left-to-icon {
    margin-left: 0em;
    vertical-align: baseline;
}
.field-message--error {
    color: #ffffff !important;
    background-color: #ed3a03 !important;
    min-width: auto !important;
}

.feedback {
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
}

.feedback.feedback--error {
    text-align: center;
    color: #ffffff;
    background-color: #a31300;
    display: block;
    width: 100%;
    padding: 10px 5px;
    box-sizing: border-box;
    font-size: 14px;
}
.feedback.feedback--alert {
    text-align: center;
    color: #ffffff;
    background-color: #eeb91c;
    display: block;
    width: 100%;
    padding: 10px 5px;
    box-sizing: border-box;
    font-size: 14px;
}

.branding-template-primary-button {
    color: #ffffff !important;
    background: transparent !important;
    border-color: #ffffff !important;
    height:36px;
    min-height: 36px !important;
    font-weight: 600;
}
.button--primary{
    color: #999999 !important;
    background: #ffffff !important;
    border-color: #ffffff !important;
}
.buttons-group {
    display: flex;
    flex-direction: row-reverse;
    gap: 5px;
    padding-top: 25px;
    padding-bottom: 5px;
}
.card{
    max-width: 350px !important;
}
.phone-input{
    max-width: initial !important;
}
.passcode-input-wrap{
    width:50%;
    margin:0px auto;
}
.text-block--small {
    line-height: 1.3em !important;
    margin: 0 0 20px !important;
}
.branding-template-link-text, a {
    color: #ffffff !important;
}
.text-block--overflow-wrap:last-child, .text-block:last-child {
    margin-bottom: 0 !important;
}
.center-text{
    text-align: center !important;
}
.heading, .heading-text {
    font-size: 20px !important;
}
.card__content.success {
    background-color: #30c437 !important;
    padding: 10px 20px 50px !important;
}
.icon-feedback--success {
    color: #ffffff !important;
}
.success .icon-feedback__label {
    color: #ffffff;
}

