module.exports = a = async ({ params }) => {
  var usableDevices = JSON.parse(params.devices);

  usableDevices = usableDevices.map(device => {
      if (device.type === "FIDO2" && device?.attributes?.previousDeviceType) {
          return { ...device, type: device.attributes.previousDeviceType }
      }
      return device;
  });

  const allowedDeviceTypes = params.allowedDeviceTypes ? params.allowedDeviceTypes.split(",") : [];

  usableDevices = usableDevices.filter(device => {
      return allowedDeviceTypes.includes(device.type);
  });

  if (params.webAuthenSupport === "NONE") {
      usableDevices = usableDevices.filter(device => {
          return device.type !== "FIDO2";
      });
  }

  usableDevices = usableDevices.filter(device => {
      return device.coolDownExpiresAt == undefined;
  });

  let emailDevices = [];
  let smsDevices = [];
  let fido2Devices = [];
  let voiceDevices = [];
  let authAppDevices = [];
  let mobileAppDevices = [];

  for (let i = 0; i < usableDevices.length; i++) {
    if (usableDevices[i].type === "EMAIL") {
      emailDevices.push(usableDevices[i]);
    } else if (usableDevices[i].type === "SMS") {
      smsDevices.push(usableDevices[i]);
    } else if (usableDevices[i].type === "FIDO2") {
      fido2Devices.push(usableDevices[i]);
    } else if (usableDevices[i].type === "VOICE") {
      voiceDevices.push(usableDevices[i]);
    } else if (usableDevices[i].type === "TOTP") {
      authAppDevices.push(usableDevices[i]);
    } else if (usableDevices[i].type === "MOBILE") {
      mobileAppDevices.push(usableDevices[i]);
    }
  }

  usableDevices = fido2Devices.concat(smsDevices, emailDevices, voiceDevices, authAppDevices, mobileAppDevices);

  return { usableDevices: usableDevices, count: usableDevices.length, canChangeDevice: usableDevices.length > 1 || params.magicLinkEnabled, usuableDeviceId: usableDevices[0].id}
}

