const setFieldFocus = (id) => {
    const element = document.getElementById(id);
    if (element) {
        element.focus();
    }
}

if('{{agreementEnabled}}'){
    let submitBtn = document.getElementById("btnRegister");
    submitBtn.setAttribute("disabled", "true");
    document.getElementById("agreeCheckBox").addEventListener("click", function(){
        if (document.getElementById("agreeCheckBox").checked == true) {
            submitBtn.removeAttribute("disabled");
        } else {
            submitBtn.setAttribute("disabled", "true");
        }
    });
}

const fetchCSS = () => {
    return fetch('https://assets.pingone.com/ux/end-user-nano/0.1.0-alpha.1/end-user-nano.css')
        .then(response => response.text())
        .catch(error => {
            console.error('Error fetching CSS:', error);
        });
};

const generatePopupHTML = (title, agreementText, logoUrl, logoStyle, companyName, cssContent) => {
    return `
    <html>
    <title>${title}</title>
    <head>
        <link rel="icon" href="https://library.pingidentity.com/images/ping-favicon.png" />
        <style>
            ${cssContent}
        </style>
    </head>
    <body>
        <div class="end-user-nano">
            <div class="bg-light d-flex flex-column justify-content-center align-items-center position-absolute top-0 start-0 bottom-0 end-0 overflow-auto">
                <div style="max-width: 500px; min-width: 500px; width: 100%; overflow-y: auto; overflow-x: none">
                    <div class="card shadow mb-5">
                        <div class="card-body p-5 d-flex flex-column">
                            <img class="align-self-center mb-5" src="${logoUrl}" style="${logoStyle}" alt="${companyName}" />
                            <h1 class="text-center mb-4">${title}</h1>
                            <div style="white-space: pre-line;">
                            ${agreementText}
                            </div>
                            <button class="btn btn-primary mb-3 mt-4" onclick="window.close()" >Close Window</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </body>
    </html>`;
};

const popupToS = (cssContent) => {
    const newWindow = window.open('', '_companyToS');
    if (!newWindow) {
        console.error("Failed to open the window");
        return;
    }

    const agreementText = `{{agreementText}}`;
    const title = `{{agreementTitle}}`;
    const logoUrl = `{{logoUrl}}`;
    const logoStyle = `{{logoStyle}}`;
    const companyName = `{{companyName}}`;

    const htmlToWrite = generatePopupHTML(title, agreementText, logoUrl, logoStyle, companyName, cssContent);

    newWindow.document.write(htmlToWrite);
    newWindow.document.close();
};

const start = async () => {
    setFieldFocus("email");
    const tosButton = document.getElementById("tosBtn");
    if (tosButton) {
        const cssContent = await fetchCSS();
        if (tosButton.addEventListener) {
            tosButton.addEventListener("click", () => popupToS(cssContent));
        } else if (tosButton.attachEvent) {
            tosButton.attachEvent("onclick", () => popupToS(cssContent));
        } else {
            tosButton.onclick = () => popupToS(cssContent);
        }
    }  
}

if (document.readyState === "loading") {
    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

