    // Loading hasn't finished yet
    document.addEventListener("DOMContentLoaded", start);
} else {
    // `DOMContentLoaded` has already fired
    start();
}

document.onclick = function(e) {
  const divToHide = document.getElementById("successMessage")
  if (divToHide != null) {
    divToHide.style.display = "none";
  }
}


var errorDiv = document.getElementById('error-wrap');
if(window.addEventListener) {
   // Normal browsers
   errorDiv.addEventListener('DOMSubtreeModified', contentChanged, false);
} else if(window.attachEvent) {
    // IE
    errorDiv.attachEvent('DOMSubtreeModified', contentChanged);
}

// Maps the password policy requirements to well-formed messages
function contentChanged() {
   let errorMsg  = document.getElementById('feedback');
   let passwordErrorMsgTitle = document.getElementById('password-error-msg-title');
   let passwordErrorMsg  = document.getElementById('password-error-msg');
   passwordErrorMsg.innerText = "";

   if (errorMsg) {
       if (errorMsg.innerText.includes("passwordPolicies:")) {
         passwordErrorMsgTitle.innerText = "Password must meet requirements";
          passwordErrorMsgTitle.style.display = "block";
          passwordErrorMsgTitle.style.marginBottom = "1rem";
          const policies = [];
          let textNode = document.createTextNode("Your password must:");
          passwordErrorMsg.appendChild(textNode);

          if (errorMsg.innerText.includes("minCharactersLowercase")) {
            policies.push("Contain at least 1 lowercase letter.");
          }
          if (errorMsg.innerText.includes("minCharactersUppercase")) {
            policies.push("Contain at least 1 uppercase letter.");
          }
          if (errorMsg.innerText.includes("minCharactersSpecialChar")) {
            policies.push("Contain at least 1 of these special characters: ~!@#$%^&*()-_=+[]{}|;:,.<>/?");
          }
          if (errorMsg.innerText.includes("minCharactersNumeric")) {
            policies.push("Contain at least 1 number.");
          }
          if (errorMsg.innerText.includes("minUniqueCharacters")) {
            policies.push("Include at least 5 unique characters.");
          }
          if (errorMsg.innerText.includes("excludesCommonlyUsed")) {
            policies.push("Not be on the list of the most commonly-used passwords.");
          }
          if (errorMsg.innerText.includes("maxRepeatedCharacters")) {
            policies.push("Not have more than 2 of the same characters in a row. For example, 'aa' is acceptable but 'aaa' is not.");
          }
          if(errorMsg.innerText.includes("history")) {
            policies.push("Be different from previous passwords.");
          }
          if (errorMsg.innerText.includes("length")) {
            // Handles parsing the length requirement in the event the length is double digits. Will need to update this if length is 3 digits. Highly uncommon.
            const errorMessage = errorMsg.innerText;
            const index = errorMessage.indexOf("length");
            if (index !== -1 && index < errorMessage.length - 1) {
  					  let length = errorMessage[index + 6];
              if (errorMessage[index + 7] !== " " && errorMessage[index + 7] !== undefined) {
                length = length + errorMessage[index + 7];
              }
              if (errorMessage[index + 8] !== " " && errorMessage[index + 8] !== undefined && errorMessage[index + 8] - '0' >= 0 && errorMessage[index + 8] - '0' <= 9) {
                length = length + errorMessage[index + 8];
                policies.push("Be at most " + length + " characters long.");
              } else {
                policies.push("Be at least " + length + " characters long.");
              }
					  }
          }

          if(policies.length === 0) policies.push("Follow password policy requirements.");

          policies.forEach(policy => {
            const li = document.createElement("li");
            li.textContent = policy;
            passwordErrorMsg.appendChild(li);
          });
            passwordErrorMsg.classList.add("input-policy-error");
            errorMsg.style.display = "none";
        } else {
            passwordErrorMsgTitle.style.display = "none";
            passwordErrorMsgTitle.style.marginBottom = "0rem";
            // leave message as-is
        }
   }
}

