// Write your code here
// Supported language: Javascript 
module.exports = a = async ({params}) => {
	let result = "";
	// Parse the password policy requirements in the detailed error message and pass it back to the form to map the proper errors.
	if (params.errorDetails &&
  		params.errorDetails.details &&
  		params.errorDetails.details[0] &&
  		params.errorDetails.details[0].rawResponse &&
  		params.errorDetails.details[0].rawResponse.details &&
  		params.errorDetails.details[0].rawResponse.details[0].innerError) {
		const details = params.errorDetails.details[0].rawResponse.details[0].innerError;
		result = "passwordPolicies:";
		for (let key in details) {
			if (details.hasOwnProperty(key) && key !== "unsatisfiedRequirements") {
				switch (key) {
					case "minCharacters":
						const reason = details[key];

						if (reason.includes("ZYXWVUTSRQPONMLKJIHGFEDCBA")) {
							result += " minCharactersUppercase";
						}
						if (reason.includes("~!@#$%^&*()-_=+[]{}|;:,.<>/?")) {
							result += " minCharactersSpecialChar";
						}
						if (reason.includes("0123456789")) {
							result += " minCharactersNumeric";
						}
						if (reason.includes("abcdefghijklmnopqrstuvwxyz")) {
							result += " minCharactersLowercase";
						}
						break;
					case "minUniqueCharacters":
						result += " minUniqueCharacters";
						break;
					case "excludesCommonlyUsed":
						result += " excludesCommonlyUsed"
						break;
					case "history":
						result += " history"
						break;
					case "length":
					// Parse the length requirement
						const value = details[key];
						const index = value.indexOf("of");
						if (index !== -1 && index < value.length - 1) {
							let lengthRestriction = value[index + 3];
							if (value[index + 4] !== " ") {
								lengthRestriction = lengthRestriction + value[index + 4];
							}
							if (value[index + 5] !== " " && value[index + 5] - '0' >= 0 && value[index + 5] - '0' <= 9) {
								lengthRestriction = lengthRestriction + value[index + 5];
							}
							result += " length" + lengthRestriction;
						}
						break;
					case "maxRepeatedCharacters":
						result += " maxRepeatedCharacters";	
						break;	
					default:
						result += details[key];					

				}
			}
		}
	} else {
		let errorMessage = params.errorDetails.message;
		let index = errorMessage.indexOf(':');
		if (index !== -1) result = errorMessage.substring(index + 2);
		else result = errorMessage;
	}

	return {'updatedErrorMessage': result.replace('username', 'email address')}
}

