module.exports = a = async ({ params }) => {
    var usableDevices = JSON.parse(params.usableDevices);

    const allowedDeviceTypes = params.allowedDeviceTypes && params.allowedDeviceTypes.split(",");

    const allowSMS = allowedDeviceTypes.some(device => device === "SMS");
    const allowEmail = allowedDeviceTypes.some(device => device === "EMAIL");
    const allowFIDO2 = allowedDeviceTypes.some(device => device === "FIDO2");
    const allowTOTP = allowedDeviceTypes.some(device => device === "TOTP");
    const allowMobile = allowedDeviceTypes.some(device => device === "MOBILE");
    const allowVoice = allowedDeviceTypes.some(device => device === "VOICE");

    if (params.webAuthenSupport === "NONE") {
        usableDevices = usableDevices.filter(device => {
            return device !== "SECURITY_KEY" && device !== "PLATFORM" && device !== "FIDO2";
        });
    }

    usableDevices = usableDevices.filter(device => {
        return (device === "SMS" && allowSMS) 
            || (device === "EMAIL" && allowEmail) 
            || (device === "FIDO2" && allowFIDO2)
            || (device === "TOTP" && allowTOTP)
            || (device === "MOBILE" && allowMobile)
            || (device === "VOICE" && allowVoice);
    })

    if (params.webAuthenSupport == "SECURITY_KEY_ONLY") {
        usableDevices = usableDevices.filter(device => {
            return device !== "PLATFORM";
        });
    }

    if (usableDevices.includes("MOBILE")) {
        const index = usableDevices.indexOf("MOBILE");
        if (index !== -1) {
            usableDevices.splice(index, 1);
            usableDevices.unshift("MOBILE");
        }
    }

    if (usableDevices.includes("VOICE")) {
        const index = usableDevices.indexOf("VOICE");
        if (index !== -1) {
            usableDevices.splice(index, 1);
            usableDevices.unshift("VOICE");
        }
    }

    if (usableDevices.includes("SMS")) {
        const index = usableDevices.indexOf("SMS");
        if (index !== -1) {
            usableDevices.splice(index, 1);
            usableDevices.unshift("SMS");
        }
    }

    if (usableDevices.includes("TOTP")) {
        const index = usableDevices.indexOf("TOTP");
        if (index !== -1) {
            usableDevices.splice(index, 1);
            usableDevices.unshift("TOTP");
        }
    }

    if (usableDevices.includes("FIDO2")) {
        const index = usableDevices.indexOf("FIDO2");
        if (index !== -1) {
            usableDevices.splice(index, 1);
            usableDevices.unshift("FIDO2");
        }
    }

    return { usableDevices: usableDevices, count: usableDevices.length }
}

