module.exports = a = async ({ params }) => {
	var selected = JSON.parse(params.devices).filter(device => {
		return device.id == params.id;
	});

	// For taking account of additional data that is contained in addDevicesData variable
	var addDevicesData = JSON.parse(params.addDevicesData).filter(addDevice => {
		return addDevice.id == params.id;
	});

	if (selected && selected.length > 0 && selected[0].hasOwnProperty("nickname")) {
  		var deviceName = selected[0].nickname;
	}

	if (selected[0].model) {
		deviceName = selected[0].model.marketingName;
		if(selected[0].manufacturer) deviceName = selected[0].manufacturer + " " + deviceName;
	}

	if (selected[0].displayName) {
		deviceName = selected[0].displayName;
	}

	var applicationName = addDevicesData[0].application ? addDevicesData[0].application.name : "";

	return {
		selectedDeviceType: selected[0].type,
		selectedDeviceEmail: selected[0].email,
		selectedDevicePhone: selected[0].phone,
		selectedDeviceAppName: applicationName,
		selectedDeviceName: deviceName,
		selectedDeviceOtpEnabled: addDevicesData[0].otpEnabled
	}
}

