code.value:
module.exports = a = async ({ params }) => {
	let authMethod = "";

	for (let i = 0; i < params.authenticators.length; i++) {
  		if (params.authenticators[i] != "mfa"){
			if(!authMethod){
				authMethod = params.authenticators[i];
			} else {
				authMethod = authMethod + " " + params.authenticators[i];
			}
		}
	}

	return { "authMethod": authMethod }
}

