customScript.value:
// Focus on Verification Code input field as soon as page is loaded
const focusOnFirstInputElement = () => {
  document.getElementById("verificationCode").focus();
};

const handleVerifyBtn = () => {
   const verificationCode = document.getElementById("verificationCode");

   if (window.addEventListener) {
      // Normal browsers
      verificationCode.addEventListener("keyup", inputChanged, false);
   } else if (window.attachEvent) {
      // IE
      verificationCode.attachEvent("keyup", inputChanged);
   }

   function checkInputValid(val) {
      return /^[0-9]{6}$/.test(val);
   }

   function inputChanged() {
      let verifyButton = document.getElementById('verifyBtn');
      if (!checkInputValid(verificationCode.value)) {
         verifyButton.setAttribute("disabled", "true");
      } else {
         verifyButton.removeAttribute("disabled");
      }
   }

   inputChanged();
};

/**
 * If page isn't loaded yet, wait for the page to load, then focus on first
 * input element.
 */
if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.onreadystatechange = () => {
    if (document.readyState === "complete") {
      focusOnFirstInputElement(); 
      // handleVerifyBtn();
    }
  };
} else {
  // `DOMContentLoaded` has already fired
  focusOnFirstInputElement(); 
//   handleVerifyBtn();
}

