/**
 *
 * Compares passwords to confirm they match when user clicks submit button
 *
 */
const comparePasswords = (e) => {
  const newPassword = document.getElementById("newPassword");
  const verifyNewPassword = document.getElementById("verifyNewPassword");
  const verifyNewPasswordSpan = document.getElementById("verifyNewPWErrMsg");

  if (
    newPassword?.value &&
    verifyNewPassword?.value &&
    newPassword.value === verifyNewPassword.value
  ) {
    verifyNewPasswordSpan.innerText = "";
    verifyNewPasswordSpan.classList.add("d-none");

    return true;
  }

  verifyNewPasswordSpan.innerText = "Passwords do not match.";
  verifyNewPasswordSpan.classList.remove("d-none");

  e.preventDefault();
  e.stopPropagation();
  return false;
};

const addEListener = (e) => {
  // `DOMContentLoaded` has already fired
  // const verifyNewPassword = document.getElementById("verifyNewPassword");
  const submitBtn = document.getElementById("submitBtn");

  if (submitBtn) {
    submitBtn.addEventListener("click", comparePasswords);
  }

  return comparePasswords();
};

/**
 * Wait for page to load to add event listener to submit button
 */
if (document.readyState === "loading") {
  // Loading hasn't finished yet
  document.addEventListener("DOMContentLoaded", addEListener);
} else {
  // `DOMContentLoaded` has already fired
  const submitBtn = document.getElementById("submitBtn");

  if (submitBtn) {
    submitBtn.addEventListener("click", comparePasswords);
  }
}

